public class brc {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("请输入一个小于50的数。");
		Scanner in = new Scanner(System.in);
		int b=in.nextInt();
		int a = 0;
			while(b>a){
			a++;
			if(a==5)continue;
			int c=a+b;
			}
			System.out.println(a);
			System.out.println(b);
			System.out.println(c);
	}
}
