
public class csbcdjj {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		int c=0;
		for(int i=1;i<=2;i++){	
			for(a=1;a<=i;a++){
				System.out.print(" ");
			}
		for(a=1;a<=3-c*2;a++){
			if(a%2!=0){
				System.out.print("*");
						}
			else if(a%2==0){
			System.out.print(" ");
			}
		}c++;
			System.out.println();
	}
	}

	}
